                        {/* FEATURES LIST */}
                        <section className="bg-black text-white rounded-[2rem] md:rounded-[3rem] p-5 md:p-12 lg:p-16 overflow-hidden relative text-left">
                            <div className="absolute inset-0 technical-grid opacity-10 pointer-events-none" />
